body, html {
    background-color: black;
    color: rgb(224, 224, 224);
}

/*
 * Container DIVs
 */
div#wikipagecontent { background-color: black; }

/*
 * Shared Styles
 */
div#page,
ul#wikiNavMenu li.sidepanel {
    background-color: rgb(32, 32, 32);
    border: 1px solid rgb(64, 64, 64);
    border-radius: 8px;
}

#content .table-of-contents,
ul#wikiNavMenu li.sidepanel {
    box-shadow: none;
}

/*
 * Feedback Box
 */
#feedback .header, #feedback .section {
    background-color: rgb(64, 64, 64) !important;
    border: 1px solid rgb(96, 96, 96) !important;
    opacity: 0.8 !important;
    filter: grayscale(0.8);
}

#feedback .message {
    text-shadow: 1px 1px 0 rgb(96, 96, 96) !important;
    color: rgb(224, 224, 224) !important;
}

#feedback textarea {
    background-color: rgb(32, 32, 32) !important;
    color: rgb(224, 224, 224) !important;
}

/*
 * Sidebar
 */
div#sidebar #logo img {
    filter: grayscale(0.5) brightness(0.8);
}

div#sidebar {
    background-color: black;
    border: none;
}

#searchform #searchinput {
    background-color: rgb(32, 32, 32);
    color: rgb(224, 224, 224);
    width: 98%;
}

#searchform div#searchbuttons > input {
    background-color: rgb(48, 48, 48);
    color: rgb(224, 224, 224);
    margin-top: 4px;
}

img#hideIcon,
img#showIcon,
#menuIcon {
    filter: brightness(0.6);
}

li.sidepanel.showSidePanel > div:before,
li.sidepanel.hideSidePanel > div:before{
    filter: invert(0.6);
}

/*
 * Links
 */
a,
a:link {
    color: rgb(180, 180, 255);
}

a:visited,
div#sidebar li.sidepanel a:visited {
    color: rgb(255, 180, 255);
}

a:hover,
a:visited:hover,
div#sidebar li.sidepanel a:hover,
div#sidebar li.sidepanel a:visited:hover {
    color: rgb(255, 180, 180);
}

a:active,
a:visited:active,
div#sidebar li.sidepanel a:active,
div#sidebar li.sidepanel a:visited:active {
    color: rgb(255, 224, 224) !important;
}

li.sidepanel > div {
    line-height: 1.75em;
}

li.sidepanel {
    line-height: 1.25em;
}

a.nonexistent,
a.nonexistent:link,
a.nonexistent:visited,
a.badinterwiki,
a.badinterwiki:link,
a.badinterwiki:visited {
    color: rgb(112, 112, 224);
}

a.nonexistent:hover,
a.badinterwiki:hover {
    color: rgb(160, 128, 224);
}

/*
 * Wiki Content
 */
#content .table-of-contents {
    background-color: rgb(48, 48, 48);
    border: 1px solid rgb(64, 64, 64);
    color: rgb(224, 224, 224);
    font-size: 80%;
    line-height: 1.5em;
}

#content pre,
#content div.tip,
#content div.note,
#content div.caution,
#content div.important,
#content div.warning {
    background-color: rgb(64, 64, 64);
    color: rgb(224, 224, 224);
    border: 1px solid rgb(48, 48, 48);
    border-radius: 8px;
}

div.codearea {
    background-color: black;
    border: 1px solid rgb(64, 64, 64);
    border-radius: 8px;
}

div.codearea a.codenumbers {
    line-height: 2em;
}

#content div.codearea pre {
    border: 0;
    margin: -1px;
}

strong.highlight {
    background-color: rgb(48, 64, 96);
    border-radius: 4px;
    padding: 4px 8px;
}

table th, table td {
    border: 1px solid rgb(96, 96, 96);
    padding: 0.4em 0.6em;
}

table th { background-color: rgb(96, 96, 96); }
table tr { background-color: rgb(16, 16, 16); }
table tr:nth-child(even) { background-color: rgb(40, 40, 40); }

/*
 * Syntax Highlighting (pygments)
 */
pre .hll { background-color: rgb(96, 96, 48); }

pre .c,
pre .c1,
pre .cm { color: rgb(128, 172, 128); }

pre .cs,
pre .k,
pre .kc,
pre .kd,
pre .kn,
pre .kp, 
pre .kr,
pre .ow,
pre .ss { color: rgb(96, 218, 218); }

pre .kt,
pre .nb,
pre .nn,
