html {
    min-height: 100%;
}
body {
    height: 100%;
    background: 0;
    color: #292b2c;
    font-family: Roboto, Arial, san-serif;
    font-size: 12pt;
}
a {
    color: #333;
}
a:hover {
    color: #555;
}
h1, h2, h3,
.gameView .gameStatusLabel,
.textInfoView .textInfoHeader,
.textInfoView .textInfoOwnStats,
.textInfoView .textInfoTitle a,
.textInfoView .textInfoOwnStatsNumber,
.tblOwnStatsNumber .datetime,
.tblOwnStatsNumber,
.textInfoView .tblOwnStats { 
    color: #292b2c !important;
}

/* Navigation */
.navigation a {
    font-family: 'Roboto', Arial, san-serif;
}

.themeHeader,
.mainUserHeader {
    background: 0;
    background-color: #292b2c;
}
.themeHeader a {
    color: #FFF;
}
.themeHeader a:hover,
.themeHeader a:focus {
    color: #EEE;
}
.themeHeader .title {
    padding: 25px;
}
.themeHeader .title h1 a {
    text-transform: uppercase;
}
.themeHeader .navigation {
    padding: 22px 20px;
    height: 0;
}
.themeHeader #tstats {
    padding-top: 10px;
}
.themeHeader #tstats .mainUserInfoBox tbody tr td {
    color: #EEE;
}
.subTitle {
    display: none;
}
/* Player Info */
.playerInfoPopup {
    background: #292b2c;
    color: #FFF;
    border-color: #222;
}
.playerInfoPopup a {
    color: #EEE;
}
.playerInfoPopup a:hover,
.playerInfoPopup a:focus {
    color: #FFF;
}

/* Content */
.main,
.themeContent {
    background: 0;
    background-color: #FFF;
    margin: 0 auto;
    max-width: 800px;
}
.mainViewportHolder,
.mainViewport {
    width: 800px;
}
.nonHideableWords, .inputPanel span {
    letter-spacing: 0.25px;
    word-spacing: 2px;
    line-height: 28px;
    font-size: 22px !important;
    text-decoration: none;
    font-family: 'Roboto Mono', Consolas, monospace;
}
/*.inputPanel span:nth-of-type(1) {
    color: #5fa7e8 !important;
}*/
[id^='nhwMiddlegwt-uid-'], [id^='nhwMiddleCommagwt-uid-'],
.acceptedChars {
    color: #2fa4e7 !important;
}
